mapper.select:
  SELECT

 FROM attribute WHERE status = 0

  and ich_category_id = #{ichCategoryId,jdbcType=BIGINT}


  and target_type = #{targetType,jdbcType=INTEGER}

order by seq

select a.cn_name from attribute a LEFT JOIN  content_fragment c on a.id = c.attribute_id
where a.status = 0

  and a.cn_name= #{cnName,jdbcType=VARCHAR}


  and c.target_id= #{targetId,jdbcType=BIGINT}
